import httpClient from '../httpClients.ts';
import Navbar from './nav';
import Cookies from 'js-cookie';
import { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { Button } from 'react-bootstrap';


function App() {
  const [isLoading, setLoading] = useState(true);
  const [message, setMessage] = useState();

  useEffect(()  => {
    httpClient.get("http://fullstacklb-1931106476.eu-central-1.elb.amazonaws.com:5000/user").then(response => {
      setMessage(response.data);
      setLoading(false)})
      .catch((error) => {
        if (error.response.status === 401) {
          Cookies.remove('session')
          window.location.href = "/login";
        }
      });

  }, []);

  if (isLoading) {
    return <div className="App">Loading...</div>;
  }


  return (
    <div>
    <Navbar></Navbar>
    <h1>Hi, {message.first_name}</h1>
    <Link to="/address"><Button variant="dark">set/change address</Button></Link>
    </div>

  );

}


export default App;
